'use strict';

const program = require('commander');
const {Command} = require('ripple-command/command');

program
    .command('get-account-info <address>')
    .description('Returns information for the specified account. Note: For account data that is modifiable by the user, see the get-settings command.')
    .option('--ledger-version <int>',
        'Get the account info as of this historical ledger version.')
    .action(Command.actionHandler({
        apiCall: 'getAccountInfo',
        optionsMap: {
            'int': ['ledgerVersion'],
        }
    }))
    ;
