'use strict';

const program = require('commander');
const {Command} = require('ripple-command/command');

program
    .command('get-balances <address>')

    .description('Returns balances for a specified account.')

    .option('--counterparty <address>',
        'Only return balances with this counterparty.')

    .option('--currency <currency>',
        'Only return balances for this currency.')

    .option('--ledger-version <int>',
        'Return balances as they were in this historical ledger version.')

    .option('--limit <int>',
        'Return at most this many balances.')

    .action(Command.actionHandler({
        apiCall: 'getBalances',
        optionsMap: {
            'int': ['limit', 'ledgerVersion'],
        }
    }))
;
