'use strict';

var program = require('commander');
var {Config} = require('ripple-command/config');
var {Console} = require('ripple-command/console');
var {RippleWrapper} = require('ripple-command/api/ripple-wrapper');

program
    .command('get-fee')
    .description('Returns the estimated transaction fee (in XRP) for the currently configured server.')
    .action(function(command) {
        Config.absorb(program.opts(), command.opts());
        (new RippleWrapper()).invoke('getFee').then((fee) => {
            Console.out('Ripple Server Fee:');
            Console.out(fee, 1);
        });
    })
    ;
