'use strict';

const program = require('commander');
const {Command} = require('ripple-command/command');

program
    .command('get-ledger')
    .description('Returns header information for the specified ledger (or the most recent validated ledger if no ledger is specified).')
    .option('--ledger-version <int>',
        'Get ledger data for this historical ledger version.')
    .option('--include-all-data',
        'Include full transactions and/or state information if --include-transactions and/or --include-state is set.')
    .option('--include-state',
        'Return an array of hashes or all state data in this ledger version, depending on whether --include-all-data is set.')
    .option('--include-transactions',
        'Return hashes for each transaction or all transactions that were validated in this ledger version, depending on whether --include-all-data is set.')
    .action(Command.actionHandler({
        apiCall: 'getLedger',
        optionsMap: {
            'int': ['ledgerVersion'],
        }
    }))
    ;
