'use strict';

const program = require('commander');
const {Command} = require('ripple-command/command');

program
    .command('get-settings <address>')
    .description('Returns settings for the specified account. Note: For account data that is not modifiable by the user, see the get-account-info command.')
    .option('--ledger-version <int>',
        'Get the settings as of this historical ledger version.')
    .action(Command.actionHandler({
        apiCall: 'getSettings',
        optionsMap: {
            'int': ['ledgerVersion'],
        }
    }))
    ;
