'use strict';

const program = require('commander');
const {Command} = require('ripple-command/command');

program
    .command('get-transaction <id>')

    .description('Retrieves a transaction by its Transaction ID.')

    .option('--max-ledger-version <max>',
        'The highest ledger version to search')

    .option('--min-ledger-version <min>',
        'The lowest ledger version to search.')

    .action(Command.actionHandler({
        apiCall: 'getTransaction',
        optionsMap: {
            'int': ['maxLedgerVersion', 'minLedgerVersion'],
        },
    }))
;
