'use strict';

var _ = require('lodash');

const defaults = {
    server: 'wss://s1.ripple.com',
    offline: false,
};

class Config {

    constructor() {
        this.config = _.extend({}, defaults);
    }

    get(name) {
        return this.config[name];
    }

    getDefault(name) {
        return defaults[name];
    }

    set(name, value) {
        this.config[name] = value;
    }

    absorb(...options) {
        this.config = _.extend.apply(this.config, options);
    }

}

module.exports = {
    Config: new Config(),
};
