'use strict'

// load .env vars into process.env
require('dotenv').config();

// set up winston for logging
const {Config} = require('ripple-command/config');
if (Config.get('logging')) {
    const winston = require('winston');
    winston.remove(winston.transports.Console);
    winston.add(winston.transports.File, { filename: Config.get('logfile') });
}

// load all program commands
require('ripple-command/program');

// extra line for readability
console.log('');

// process input according to commands
var program = require('commander');
program.parse(process.argv);

// if nothing was passed, show the help
if (!program.args.length) { program.help(); }

